<?php
ini_set('display_errors', 'On');
include_once("VDMQueryChecker.php");
/**
 *
 */
class Pmapi{
	protected static $TYPES=["getBacteriaList","getBacteria","getExperiment","getPlate","getGrowth"];
	private static $ERR_JSON='{"success":false,"error_message":"%MSG%","error_code":%CODE%}';

	public static function process(){
		$query=$_SERVER["REQUEST_METHOD"]==="GET"?$_GET:$_POST;
		try{
			$query=VDMQueryChecker::checkToken($query);
			VDMQueryChecker::checkType($query["type"],self::$TYPES);
			$query["param"]=VDMQueryChecker::checkParam($query["param"]);
		}catch(Exception $e){
			echo self::replaceErr($e);
			exit();
		}
		switch($query["type"]){
			case self::$TYPES[0]:
				echo self::getBacteriaList();
				break;
			case self::$TYPES[1]:
				echo self::getBacteria($query["param"]);
				break;
			case self::$TYPES[2]:
				echo self::getExperiment($query["param"]);
				break;
			case self::$TYPES[3]:
				echo self::getPlate($query["param"]);
				break;
			default:
				echo "{test}";
		}
	}

	

	private static function replaceErr($e){
		return str_replace("%CODE%",$e->getCode(),str_replace("%MSG%",$e->getMessage(),self::$ERR_JSON));
	}

	private static function getBacteriaList(){
		return '{"data":{"R.S.1":{"bacteria_id":69,"bacteria_external_id":"R.S.1","bacteria_name":"Alcaligenes faecalis 371","vc_id":0,"vector":null},"R.S.2":{"bacteria_id":70,"bacteria_external_id":"R.S.2","bacteria_name":"Bacillus subtilis 845","vc_id":0,"vector":null}},"success":true,"error_message":null,"error_code":null,"mid":0}';
	}

	private static function getBacteria($beids){
		$temp=json_decode('{"R.S.1":{"bacteria_id":69,"bacteria_external_id":"R.S.1","bacteria_name":"Alcaligenes faecalis 371","vc_id":0,"vector":null},"R.S.2":{"bacteria_id":70,"bacteria_external_id":"R.S.2","bacteria_name":"Bacillus subtilis 845","vc_id":0,"vector":null}}');
		$data=json_decode('{"success":true,"error_message":null,"error_code":null,"mid":0}');
		$data['data']=[];
		foreach($id in $beids)
			$data['data'][$id]=$temp[$id];
		echo json_encode($data);
	}

